mapper.select:
     select lg_id, lg_menu, lg_type, ref_u_id, lg_obj_id, lg_obj_desc, lg_ip, lg_createtime, lg_desc
	   from user_log
	  where 1=1


		AND ref_u_id in

					#{item}



	and lg_menu 		like 	CONCAT('%',#{lg_menu},'%')		
	and ref_u_id 		= 		#{ref_u_id}						
	and lg_type 		like 	CONCAT('%',#{lg_type},'%')		
	and lg_obj_id 	like 	CONCAT('%',#{lg_obj_id},'%')	
	and lg_obj_desc 	like 	CONCAT('%',#{lg_obj_desc},'%')	
	and lg_ip 		like 	CONCAT('%',#{lg_ip},'%')		
	and lg_createtime >= 	#{lg_createtime_begin}			
	and lg_createtime <= 	#{lg_createtime_end}			
	and lg_desc 		like 	CONCAT('%',#{lg_desc},'%')		


	  order by ${sort} 

			${order}


	limit ${offset}, ${limit}
